// src/components/ContactForm.jsx
import React, { useState } from 'react';
import { toast } from 'react-toastify';

export default function ContactForm({ onAdd }) {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [tags, setTags] = useState('');
    const [profilePic, setProfilePic] = useState('');
    const [profilePicPreview, setProfilePicPreview] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        //Check phone is digits only
        const phoneDigits = phone.replace(/\D/g, '');
        if (phoneDigits.length < 9) {
            toast.info('Phone number must be at least 9 digits');
            return;
        }

        const newContact = { name, email, phone, tags, profilePic };

        try {
            const res = await fetch('http://localhost:3001/contacts', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(newContact)
            });

            if (res.ok) {
                setName('');
                setEmail('');
                setPhone('');
                setTags('');
                setProfilePic('');
                setProfilePicPreview('');
                onAdd(); // refresh list
                toast.success('✅ Contact added successfully!');
            } else {
                toast.error('❌ Server error while adding contact.');
            }
        } catch (err) {
            console.error('Error adding contact:', err);
            toast.error('❌ Could not connect to the server.');
        }
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-3 mb-6">
            <input
                className="w-full border p-2 rounded"
                type="text"
                placeholder="Name"
                value={name}
                onChange={(e) => setName(e.target.value)} />
            <input
                className="w-full border p-2 rounded"
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)} />
            <input
                className="w-full border p-2 rounded"
                type="text"
                placeholder="Phone"
                value={phone}
                onChange={(e) => setPhone(e.target.value)} />
            <input
                className="w-full border p-2 rounded"
                type="text"
                placeholder="Tags (comma separated: Family, Work)"
                value={tags}
                onChange={e => setTags(e.target.value)}
            />

            <div>
                <label className="block mb-1 font-semibold">Profile Picture</label>
                <input
                    type="file"
                    accept="image/*"
                    onChange={async (e) => {
                        const file = e.target.files[0];
                        if (file) {
                            // Show local preview
                            setProfilePicPreview(URL.createObjectURL(file));
                            // Prepare upload
                            const formData = new FormData();
                            formData.append('profilePic', file);
                            // Upload to backend
                            const res = await fetch('http://localhost:3001/upload', {
                                method: 'POST',
                                body: formData,
                            });
                            const data = await res.json();
                            setProfilePic(data.filename); // Save filename for backend
                        }
                    }}
                    className="w-full"
                />
                {/* Show preview */}
                <div className="mt-2">
                    {profilePicPreview ? (
                        <img src={profilePicPreview} alt="Profile Preview" className="w-16 h-16 rounded-full border" />
                    ) : (
                        <span className="block text-xs text-gray-400">No picture selected</span>
                    )}
                </div>
            </div>



            <button
                type="submit"
                className="bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700">
                ➕ Add Contact
            </button>
        </form>
    );
}
