import { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [contacts, setContacts] = useState([]);
  const [form, setForm] = useState({ name: '', email: '', phone: '' });

  // Fetch contacts from backend
  const getContacts = async () => {
    try {
      const res = await axios.get('http://localhost:3001/contacts');
      setContacts(res.data);
    } catch (err) {
      console.error('Error fetching contacts:', err);
    }
  };

  // Add contact to backend
  const addContact = async () => {
    if (!form.name || !form.email || !form.phone) return;
    try {
      await axios.post('http://localhost:3001/contacts', form);
      setForm({ name: '', email: '', phone: '' });
      getContacts(); // refresh the list
    } catch (err) {
      console.error('Error adding contact:', err);
    }
  };

  useEffect(() => {
    getContacts();
  }, []);

  return (
    <div className="p-8 max-w-md mx-auto">
      <h1 className="text-2xl font-bold mb-4 text-center text-blue-700">ðŸ“‡ Contact Manager</h1>

      <div className="space-y-2 mb-6">
        <input
          className="w-full border p-2 rounded"
          placeholder="Name"
          value={form.name}
          onChange={e => setForm({ ...form, name: e.target.value })}
        />
        <input
          className="w-full border p-2 rounded"
          placeholder="Email"
          value={form.email}
          onChange={e => setForm({ ...form, email: e.target.value })}
        />
        <input
          className="w-full border p-2 rounded"
          placeholder="Phone"
          value={form.phone}
          onChange={e => setForm({ ...form, phone: e.target.value })}
        />
        <button
          onClick={addContact}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          âž• Add Contact
        </button>
      </div>

      <ul className="divide-y">
        {contacts.map((contact) => (
          <li key={contact.id} className="py-2">
            <div className="font-semibold">{contact.name}</div>
            <div className="text-sm text-gray-600">
              {contact.email} | {contact.phone}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
