// src/components/ContactCard.jsx
import React, { useState } from 'react';

function highlightText(text, highlight) {
    if (!highlight) return text;
    const regex = new RegExp(`(${highlight})`, "gi");
    return text.split(regex).map((part, i) =>
        regex.test(part)
            ? <span key={i} className="bg-yellow-200 rounded px-1">{part}</span>
            : part
    );
}

export default function ContactCard({ contact, onDelete, onUpdate, search }) {
    const [isEditing, setIsEditing] = React.useState(false);
    const [form, setForm] = useState({ ...contact });

    const handleEditClick = () => setIsEditing(true);
    const handleCancel = () => {
        setForm({ ...contact });
        setIsEditing(false);
    };

    const handleSave = () => {
        onUpdate(contact.id, form);
        setIsEditing(false);
    };

    const handleDelete = () => {
        if (window.confirm('Are you sure you want to delete this contact?')) {
            onDelete(contact.id);
        }
    };

    return (
        <div className="border p-4 rounded shadow-sm">
            {isEditing ? (
                <div className="space-y-2">
                    <input
                        className="w-full border p-1 rounded"
                        value={form.name}
                        onChange={(e) => setForm({ ...form, name: e.target.value })}
                    />
                    <input
                        className="w-full border p-1 rounded"
                        value={form.email}
                        onChange={(e) => setForm({ ...form, email: e.target.value })}
                    />
                    <input
                        className="w-full border p-1 rounded"
                        value={form.phone}
                        onChange={(e) => setForm({ ...form, phone: e.target.value })}
                    />
                    <div className="flex space-x-2">
                        <button
                            onClick={handleSave}
                            className="bg-green-600 text-white px-3 py-1 rounded">üíæ Save</button>
                        <button
                            onClick={handleCancel}
                            className="bg-gray-400 text-white px-3 py-1 rounded">Cancel</button>
                    </div>
                </div>
            ) : (
                <div className="flex justify-between items-center">
                    <div>
                        <h2 className="font-semibold text-lg">
                            {highlightText(contact.name, search)}</h2>
                        <p className="text-gray-600">
                            {highlightText(contact.email, search)} |
                            {highlightText(contact.phone, search)}
                        </p>
                    </div>

                    <div className="flex space-x-2">
                        <button
                            className={contact.favorite ? "text-yellow-400 text-2xl" : "text-gray-400 text-2xl"}
                            onClick={() => onUpdate(contact.id, { ...contact, favorite: !contact.favorite })}
                            title={contact.favorite ? "Remove from favorites" : "Add to favorites"}
                            style={{ background: 'none', border: 'none', cursor: 'pointer', outline: 'none' }}
                        >
                            {contact.favorite ? "‚òÖ" : "‚òÜ"}
                        </button>
                        <button
                            onClick={handleEditClick}
                            className="bg-yellow-500 text-white px-3 py-1 rounded">‚úèÔ∏è Edit</button>
                        <button
                            onClick={handleDelete}
                            className="bg-red-500 text-white px-3 py-1 rounded">üóëÔ∏è</button>

                    </div>
                </div>
            )}
        </div>
    );
}
