import React, { useState, useEffect } from "react";
import ContactForm from "./components/ContactForm";
import ContactList from "./components/ContactList";
import SearchBar from "./components/SearchBar";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default function App() {

  const [contacts, setContacts] = useState([]);
  const [search, setSearch] = useState("");
  const [tagFilter, setTagFilter] = useState('');

  const fetchContacts = async () => {
    try {
      const response = await fetch("http://localhost:3001/contacts");
      const data = await response.json();
      setContacts(data);
    } catch (error) {
      console.error("Error fetching contacts:", error);
    }
  };

  const deleteContact = async (id) => {
    try {
      await fetch(`http://localhost:3001/contacts/${id}`, {
        method: 'DELETE'
      });
      fetchContacts(); // Refresh the list
    } catch (err) {
      console.error('Error deleting contact:', err);
    }
  };

  const updateContact = async (id, updatedData) => {
    try {
      await fetch(`http://localhost:3001/contacts/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updatedData)
      });
      fetchContacts(); // refresh list
    } catch (err) {
      console.error('Error updating contact:', err);
    }
  };

  //Collect all unique tags from contact(no duplicates)
  const allTags = Array.from(
    new Set(
      contacts
        .flatMap(c => (c.tags ? c.tags.split(',').map(tag => tag.trim()) : []))
        .filter(Boolean)
    )
  );

  const filteredContacts = contacts
    .filter(c => c.name.toLowerCase().includes(search.toLowerCase()) ||
      c.email.toLowerCase().includes(search.toLowerCase()) ||
      c.phone.includes(search)
    )
    .filter(
      c =>
        !tagFilter || (c.tags && c.tags.split(',').map(tag => tag.trim()).includes(tagFilter))

    );


  useEffect(() => {
    fetchContacts();
  }, []);

  return (
    <div className="p-4 sm:p-8 max-w-4xl w-full mx-auto">
      <h1 className="text-3xl font-bold text-blue-600 mb-4">ðŸ“‡ Contact Manager</h1>
      <ToastContainer />
      <ContactForm onAdd={fetchContacts} />
      <SearchBar search={search} setSearch={setSearch} />
      <div className="mb-4 flex items-center gap-2">
        <label htmlFor="tagFilter" className="font-semibold">Filter by Tag:</label>
        <select
          id="tagFilter"
          className="border p-2 rounded"
          value={tagFilter}
          onChange={e => setTagFilter(e.target.value)}
        >
          <option value="">All</option>
          {allTags.map(tag => (
            <option key={tag} value={tag}>{tag}</option>
          ))}
        </select>
      </div>

      <ContactList
        contacts={filteredContacts}
        onDelete={deleteContact}
        onUpdate={updateContact}
        search={search}
      />

    </div>
  );


}
