// src/components/ContactForm.jsx
import React, { useState } from 'react';
import { toast } from 'react-toastify';

export default function ContactForm({ onAdd }) {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        //Check phone is digits only
        const phoneDigits = phone.replace(/\D/g, '');
        if (phoneDigits.length < 9) {
            toast.success('Phone number must be at least 9 digits');
            return;
        }

        const newContact = { name, email, phone };

        try {
            const res = await fetch('http://localhost:3001/contacts', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(newContact)
            });

            if (res.ok) {
                setName('');
                setEmail('');
                setPhone('');
                onAdd(); // refresh list
            } else {
                toast.success('❌ Server error while adding contact.');
            }
        } catch (err) {
            console.error('Error adding contact:', err);
            toast.error('❌ Could not connect to the server.');
        }
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-3 mb-6">
            <input className="w-full border p-2 rounded" type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} />
            <input className="w-full border p-2 rounded" type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
            <input className="w-full border p-2 rounded" type="text" placeholder="Phone" value={phone} onChange={(e) => setPhone(e.target.value)} />
            <button type="submit" className="bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700">
                ➕ Add Contact
            </button>
        </form>
    );
}
